import UIKit

typealias StatViewModel = Stats.Fetch.ViewModel.Item
typealias StatCellType = Stats.Fetch.ViewModel.CellType
typealias ChartEntry = Stats.Fetch.ViewModel.ChartEntry

enum Stats
{
    // MARK: Use cases
    
    enum Fetch
    {
        struct Request
        {
        }
        struct Response
        {
            var currentUserInfo : CurrentUser?
        }
        struct ViewModel
        {
            var statList : [StatCellType]?
            var errorMsg : String?
            
            struct Item {
                var title : String?
                var image : UIImage?
                var value : Int = 0
                var chartEntries = [ChartEntry]()
            }
            
            enum ChartEntry {
                case line([String],[Double])
                case hologram([String],[Double])
                
                var title : String {
                    switch self {
                    case .line:
                        return "par jour"
                    case .hologram:
                        return "par mati√®re"
                    }
                }
            }
            
            enum CellType {
                case chart(StatViewModel)
                case stat(StatViewModel)
            }
        }
    }
}
